from Crypto.Util.number import isPrime 
from random import randint
from math import gcd

def genkey():
    e = 11
    a=d=2
    # i love dirichlet!
    while gcd(a,d)!=1:
        a = randint(1,1<<512)
        d = randint(1,1<<510)
    p = a
    while p%e==1 or not isPrime(p):
        p += d
    q = p+d
    while q%e==1 or not isPrime(q):
        q += d
    print(f"Oh no a secret leak: {d}")
    return (p,q,e*2) # im feeling 22~

def enc(m,n,e):
    m = int.from_bytes(m, 'big')
    n = p*q
    c = pow(m,e,n)
    return c

