from Crypto.Util.number import long_to_bytes, bytes_to_long, getPrime

flag = "flag{XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX}"
flag = flag.encode()
flag += (128 - len(flag)) * b"\xfe"
m = bytes_to_long(flag)
p,q = getPrime(512), getPrime(512)
n = p*q

e1 = 65537
e2 = 65535
c1 = pow(m, e1, n)
c2 = pow(m, e2, n)

print("c1 =", c1)
print("c2 =", c2)
print("n  =", n)
