from Crypto.Util.number import long_to_bytes, bytes_to_long, getPrime, isPrime
import random

e = 0x10001

flag = b"flag{XXXXXXXXXXXXXXXXXXXXXXXXXXXXXX}"
flag += (128 - len(flag)) * b"\xfe"
m = bytes_to_long(flag)

prime_pool = [getPrime(512) for _ in range(100)]
ns, cs = [], []
for _ in range(12):
    i,j = random.randint(0,99), random.randint(0,99)
    p,q = prime_pool[i], prime_pool[j]
    n = p*q
    c = pow(m, e, n)
    ns.append(n), cs.append(c)
    
print("ns =", ns)
print("cs =", cs)
